mapper.select:


     and dj_number = #{djNumber}
     and a.create_by = #{createBy}
     and store_code = #{storeCode}
     and dj_time = #{djTime}
     and dj_time >= #{beginTime}
     and dj_time <= #{endTime}
     and store_name like concat('%', #{storeName}, '%')
     and status = #{status}
    ${dataScope}

order by a.create_time desc



     and fp_number is not null 
     and fp_number is null 
     and dj_number like concat('%', #{djNumber}, '%')
     and a.create_by = #{createBy}
     and store_code = #{storeCode}
     and dj_time = #{djTime}
     and dj_time >= #{beginTime}
     and dj_time <= #{endTime}
     and person_name like concat('%', #{personName}, '%')
     and status = #{status}
    ${dataScope}

order by a.create_time desc

select count(id) from cg_rkd_single
where dj_number = #{djNumber}

    and id != #{id}

select distinct a.*,
case a.status when 0 then '正操作' when 1 then '未接收' when -1 then '已拒绝' when -2 then '被退回' when 2 then '已接收' when 3 then '已审核' end as dj_status_name,
(select sum(goods_num) from cg_rkd_single_child where dj_number=a.dj_number) as sum_num,
(select nick_name from sys_user where user_name=a.create_by) as create_name,
b.goods_num,b.goods_name,b.goods_dw,b.goods_address,b.goods_price,b.goods_money
from cg_rkd_single a join cg_rkd_single_child b on a.dj_number=b.dj_number
join sys_user u on a.create_by=u.user_name
left join sys_dept d on u.dept_id = d.dept_id

     and dj_number = #{djNumber}
     and a.create_by = #{createBy}
     and store_code = #{storeCode}
     and dj_time = #{djTime}
     and store_name like concat('%', #{storeName}, '%')
     and status = #{status}
    ${dataScope}

order by a.create_time desc

mapper.update:
update cg_rkd_single
person_name = #{personName}, person_code = #{personCode}, dj_number = #{djNumber}, store_code = #{storeCode}, dj_time = #{djTime}, store_name = #{storeName}, status = #{status}, is_rate = #{isRate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, remark = #{remark}, pay_type=#{payType}, fp_number = #{fpNumber}, fp_account = #{fpAccount}, fp_bank = #{fpBank}, fp_rate = #{fpRate}, fp_num = #{fpNum}, fp_time = #{fpTime}, file_name=#{fileName}, update_time = sysdate()
where id = #{id}
