mapper.select:


     and dj_number = #{djNumber}
     and dj_time = #{djTime}
     and dj_time >= #{beginTime}
     and dj_time <= #{endTime}
     and status = #{status}
     and file_name like concat('%', #{fileName}, '%')
    ${dataScope}

order by a.create_time desc

mapper.update:
update cgdd_info
dj_number = #{djNumber}, dj_time = #{djTime}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, file_name = #{fileName},
where id = #{id}
